REPLACE INTO `art_description` (`id`, `name`, `author`, `age`, `description`, `url`) VALUES
	(1, 'Чёрный квадрат', 'К.С. Малевич', '1915', 'Чёрный супрематический квадрат — работа Казимира Малевича, созданная в 1915 году, считается magnum opus (лат. великая работа) художника, его наиболее значительным произведением в концептуальном смысле. Это одна из самых обсуждаемых и самых известных картин в мировом искусстве.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/d9/%D0%A7%D1%91%D1%80%D0%BD%D1%8B%D0%B9_%D1%81%D1%83%D0%BF%D1%80%D0%B5%D0%BC%D0%B0%D1%82%D0%B8%D1%87%D0%B5%D1%81%D0%BA%D0%B8%D0%B9_%D0%BA%D0%B2%D0%B0%D0%B4%D1%80%D0%B0%D1%82._1915._%D0%93%D0%A2%D0%93.png/1024px-%D0%A7%D1%91%D1%80%D0%BD%D1%8B%D0%B9_%D1%81%D1%83%D0%BF%D1%80%D0%B5%D0%BC%D0%B0%D1%82%D0%B8%D1%87%D0%B5%D1%81%D0%BA%D0%B8%D0%B9_%D0%BA%D0%B2%D0%B0%D0%B4%D1%80%D0%B0%D1%82._1915._%D0%93%D0%A2%D0%93.png'),
	(2, 'Белое на белом', 'К.С. Малевич', '1918', '«Белое на белом» — картина, написанная в 1918 году Казимиром Малевичем, принадлежит к направлению русской беспредметной живописи, названному Малевичем супрематизмом.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/4c/White_on_White_%28Malevich%2C_1918%29.png/1024px-White_on_White_%28Malevich%2C_1918%29.png'),
	(3, 'Чёрный круг', 'К.С. Малевич', '1923', 'Картина принадлежит к направлению русской беспредметной живописи, названному К. С. Малевичем супрематизмом, или «новым живописным реализмом».', 'https://upload.wikimedia.org/wikipedia/commons/9/98/%D0%A7%D1%91%D1%80%D0%BD%D1%8B%D0%B9_%D0%BA%D1%80%D1%83%D0%B3.png'),
	(4, 'Скачет красная конница', 'К.С. Малевич', '1928 - 1932', 'Картина долгое время была единственной из абстрактных работ художника, признаваемая официальной историей советского искусства, чему способствовало её название и изображение событий Октябрьской революции.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/23/Red_Cavalry_Riding.jpg/1920px-Red_Cavalry_Riding.jpg'),
	(5, 'Супрематическая композиция', 'К.С. Малевич', '1916', 'Супрематическая композиция (синий прямоугольник поверх красного луча) — картина Казимира Малевича 1916 года, самое дорогое произведение русского искусства.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/1/13/Suprematist_Composition_-_Kazimir_Malevich.jpg/800px-Suprematist_Composition_-_Kazimir_Malevich.jpg'),
	(6, 'Красный квадрат', 'К.С. Малевич', '1915', 'Впервые представлен публике на выставке «0,10» 1915 года. В каталоге выставки 1915 года картина указана под названием «Живописный реализм крестьянки в двух измерениях». На обороте картины — сокращённое название: «Женщина в двух измерениях».', 'https://upload.wikimedia.org/wikipedia/commons/0/0a/Red_Square._Visual_Realism_of_a_Peasant_Woman_in_Two_Dimensions.jpg'),
	(7, 'Ленинская трибуна', 'Л.М. Лисицкий ', '1920', 'Об этой работе сам художник так писал одному из своих друзей: «Поставленная диагонально железная конструкция несет подвижные, способные перемещаться балконы — сверху для оратора и в середине — для гостей. Лифт обеспечивает подъем. Сверху установлена плоскость, которая днем служит для размещения лозунгов, а вечером используется как экран».', 'https://upload.wikimedia.org/wikipedia/commons/1/1d/El_Lissitzky%2C_Lenin_Tribune%2C_1920._State_Tretyakov_Gallery%2C_Moscow.jpg'),
	(8, 'Дизайн', 'Л.М. Лисицкий ', '1922', 'Лисицкий в равной степени может рассматриваться как художник советского авангарда 1920-х, один из основателей еврейского модернизма в 1916–1919 и классический представитель интернационального европейского авангарда 1920-х.', 'https://upload.wikimedia.org/wikipedia/commons/9/9e/Design_by_El_Lissitzky_1922.jpg'),
	(9, 'Проун 19D', 'Л.М. Лисицкий ', '1922', 'Импульсом к рождению проунов послужило задание К.С.Малевича, данное Лисицкому в Витебске, ‒ перевод супрематизма в объёмные формы: трёхмерные построения должны были заложить основы архитектурной концепции будущего.', 'https://upload.wikimedia.org/wikipedia/commons/7/75/NY_Moma_lissitsky_19D.JPG'),
	(10, 'Kestnermappe Proun, Rob. Levnis and Chapman GmbH Hannover #5', 'Л.М. Лисицкий ', '1923', 'Лазарь Мордухович Лисицкий родился в 1890 году в Смоленский губернии. В 1919 году по приглашению Марка Шагала переехал в Витебск, где вместе с ним учил живописи в Народном художественном училище.', 'https://upload.wikimedia.org/wikipedia/commons/b/b9/Lazar_El_Lissitzky_-_Kestnermappe_Proun%2C_Rob._Levnis_and_Chapman_GmbH_Hannover_-5_-_Google_Art_Project.jpg'),
	(11, 'Ängstliche Litho', 'Л.М. Лисицкий ', '1923', 'В 1923 встречается в Берлине с В.В.Маяковским и оформляет его сборник «Для голоса». Это издание станет классическим образцом современной полиграфии. На Большой берлинской художественной выставке экспонируется «Пространство проунов», возможно, первая инсталляция в искусстве ХХ века.\r\n', 'https://upload.wikimedia.org/wikipedia/commons/9/92/El_Lissitzky_%C3%84ngstliche_Litho_1923.jpg'),
	(12, 'Проун', 'Л.М. Лисицкий ', '1924', 'Проун (проект утверждения нового) – неологизм, изобретённый Л.М.Лисицким для обозначения изобретённой им новой художественной системы, соединявшей идею геометрической плоскости с законами конструктивного построения объёмной формы.', 'https://upload.wikimedia.org/wikipedia/commons/5/55/El_Lissitzky_Proun_1924.jpg'),
	(13, 'Клином красным бей белых', 'Л.М. Лисицкий ', '1919 - 1920', '«Клином красным бей белых» — пропагандистский плакат советского художника Эль Лисицкого, созданный им в технике литографии в 1919—1920 годах. Впервые был издан в 1920 году в Витебске Литиздатом Политуправления Запфронта (БССР).\r\n', 'https://upload.wikimedia.org/wikipedia/commons/f/fd/Klinom_Krasnym_Bej_Belych.JPG'),
	(14, 'Проун 99', 'Л.М. Лисицкий ', '1924', 'Комбинируя геометрические плоскости с трёхмерными объектами, Лисицкий выстраивал идеальные динамичные конструкции, парящие в пространстве, не имеющие ни верха, ни низа.', 'https://monoskop.org/images/1/15/Lissitzky_El_1924_Proun_99.jpg'),
	(15, 'Проун 4Б', 'Л.М. Лисицкий ', '1920', '«Оказалась уничтоженной единственная перпендикулярная к горизонту ось картины. Вращая проун, мы ввинчиваем себя в пространство» (Эль Лисицкий. Проун. Не мировидение ‒ но мирореальность // А.Канцедикас, З.Яргина. Эль Лисицкий. Фильм жизни. 1890–1941. М., 2004. С.32).', 'https://uploads2.wikiart.org/images/el-lissitzky/proun-4-b.jpg'),
	(16, 'Проун 5А', 'Л.М. Лисицкий ', '1919', 'В проунах использовались эстетизированные мотивы технического конструирования и приёмы начертательной геометрии, совмещались перспективы с разными точками схода. Колорит проунов был элегантно сдержанным; цвет обозначал массу, плотность и фактуру разных материалов ‒ стекла, металла, бетона, дерева. «Цвет становится барометром материала и движет его к дальнейшим перевоплощениям» (Эль Лисицкий. Проун. С.32).', 'https://uploads7.wikiart.org/images/el-lissitzky/proun-5-a-1919.jpg'),
	(17, 'Проун 1С', 'Л.М. Лисицкий ', '1919', 'Виртуозное превращение плоскости в объём и обратно, зрительное растворение плоскости, эффект прозрачности, взаимопроникновение объёмных и плоских фигур в рамках проуна решали задачу создания новой пластической концепции взаимоотношений объекта с пространством.', 'https://uploads7.wikiart.org/images/el-lissitzky/proun-1-c-1919.jpg'),
	(18, 'Проун 3А', 'Л.М. Лисицкий ', '1920', 'Мотивы проунов часто повторялись и варьировались в разных техниках ‒ станковой графике, живописи и литографии. Живописных проунов сохранилось немного, некоторые известны только по фотографии Лисицкого в его мастерской в Витебске в 1920. Первой манифестацией проунов стало издание в 1921 литографированной «Папки проунов», состоявшей из 11 листов и текста-декларации.', 'https://s3-us-west-2.amazonaws.com/collections.lacma.org-images/remote_images/ma-141034-WEB.jpg?H33DmrpE69c_c3rURBtI5A7vKRIA.FpL'),
	(19, 'Проун 1Е (Город)', 'Л.М. Лисицкий ', '1920', 'Основу художественной концепции проунов составляла интеграция приёмов архитектурного мышления в пластические принципы станковой геометрической абстракции. Тем самым расширялись границы и того и другого: условность картинной плоскости позволяла воплотить любую степень смелости архитектурной утопии, а инженерная убедительность конструкции придавала картине статус футурологического проекта.', 'https://upload.wikimedia.org/wikipedia/commons/8/85/%D0%9B%D0%B0%D0%B7%D0%B0%D1%80%D1%8C_%D0%9B%D0%B8%D1%81%D0%B8%D1%86%D0%BA%D0%B8%D0%B9_-_%D0%93%D0%BE%D1%80%D0%BE%D0%B4_%28%D0%A1%D1%83%D0%BF%D1%80%D0%B5%D0%BC%D0%B0%D1%82%D0%B8%D0%B7%D0%BC%29.jpg'),
	(20, 'Новый человек', 'Л.М. Лисицкий ', '1923', 'Произведение относится к берлинскому периоду карьеры художника. Фигура индивидуума новой эпохи, как и полагали создатели течения «конструктивизм» — это целеустремленная личность,то есть человек логики, функции и полезности. Эль Лисицкий «составил» персонаж из символов своей страны и всей прогрессивной Европы.', 'https://thesketchline.com/wp-content/uploads/2019/09/5lisicky_b-744x888.jpg'),
	(21, 'Чтец', 'Л.М. Лисицкий ', '1923', 'Одна из десяти литографий, выполненных Эль Лисицким как собственные оригинальные проекты для постановки футуристической оперы «Победы над солнцем».', 'https://thesketchline.com/wp-content/uploads/2019/09/cri_000000217663.jpg'),
	(22, 'Вендинген', 'Л.М. Лисицкий ', '1921', 'Wendingen (голландский : инверсия o r Upheaval, буквально поворачивает) был архитектурным и художественным журналом, выходившим с 1918 по 1932 год. Журнал получил признание не только благодаря своему содержанию, но и благодаря замечательному квадратному формату и яркой типографии архитекторов Хендрика Вейдевельда , Эль Лисицкого , Вильмоша Хусара и других.', 'https://upload.wikimedia.org/wikipedia/commons/f/f6/Wendingen1921Lissitzky.jpg'),
	(23, 'Два квадрата: супрематическая сказка в шести конструкциях', 'Л.М. Лисицкий ', '1920 - 1921', 'Супрематистскую картину нельзя отнести к абстракции в полном понимании термина. Свой замысел автор прокомментировал надписью «Летят на землю издалека».', 'https://thesketchline.com/wp-content/uploads/2019/09/cri_000000211726.jpg'),
	(24, 'Путешественник по всем векам', 'Л.М. Лисицкий ', '1920 - 1921', 'Из созданных Э. Лисицким десяти абстрактных композиций, названных «фигуринами», эта наиболее динамичная и больше других напоминает человека. Художник полагал, что в такой авангардной пьесе-опере, как «Победа над солнцем», должны исполнять роли самодвижущиеся роботы.', 'https://thesketchline.com/wp-content/uploads/2019/09/P07142_10-757x888.jpg'),
	(25, 'Teyashim (Four billy goats)', 'Л.М. Лисицкий ', '1919', 'Дизайнерская работа Лисицкого – обложка книги Ben Zion Raskin «Four billy goats», изданной в Варшаве в 1922 году. Автор и ранее активно использовал еврейские национальные темы и символы в своем иллюстративном творчестве.', 'https://thesketchline.com/wp-content/uploads/2019/09/Design_by_El_Lissitzky_1922-1076x888.jpg'),
	(26, 'Proun R.V.', 'Л.М. Лисицкий ', '1923', 'Проуны Эль Лисицкого – это поиск первоисточника рисунка и композиции, своеобразное очищение живописи от многовековых культурных наслоений, так сказать высвобождение основ.', 'https://thesketchline.com/wp-content/uploads/2019/09/44-000601.jpg'),
	(27, 'Архитектурные фантазии: 1', 'Я. Г. Чернихов', '1933', 'Дворец научно-исследовательской лаборатории легкой индустрии. Компактное явно-конструктивное сооружение с выраженной устойчивостью. Трехцветная гамма окраски здания в холодных тонах.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_001.jpg'),
	(28, 'Архитектурные фантазии: 2', 'Я. Г. Чернихов', '1933', 'Фермовая пространственная конструкция фабрично-заводского здания. Компактное согласованное объединение стержневых конструкций и объемов. Расцветка в две четко выраженные „сочные“ краски.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_002.jpg'),
	(29, 'Архитектурные фантазии: 3', 'Я. Г. Чернихов', '1933', 'Чистоконструктивная архитектурная композиция фабрично-заводского характера. Применение контрфорсов-быков с выраженной устойчивостью. Плотная компактная окраска.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_003.jpg'),
	(30, 'Архитектурные фантазии: 4', 'Я. Г. Чернихов', '1933', 'Из цикла „Сказки индустрии“. Завод абсолютной механизации в век техники. Воплощение согласованной функциональной связи участвующих элементов сооружения. Музыкальная гармоничная расцветка.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_004.jpg'),
	(31, 'Архитектурные фантазии: 5', 'Я. Г. Чернихов', '1933', 'Демонстрация усиленных динамико-сложных архитектурных форм. Фантастическое сооружение здания специального назначения. Цветовая иллюминовка в сочных красках. Сочетание изогнутых тел.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_005.jpg'),
	(32, 'Архитектурные фантазии: 6', 'Я. Г. Чернихов', '1933', 'Музыкально-ритмическое, связно-скомплектованное сооружение в пространственном разрешении. Конструктивная игра объемов и стержней в согласной гармонии с цветовой иллюминовкой.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_006.jpg'),
	(33, 'Архитектурные фантазии: 7', 'Я. Г. Чернихов', '1933', 'Химический завод башенной системы. Композиция из цилиндрических тел и параллелепипедов. Мощная спаянная установка монолитного характера, явно устойчивого вида.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_007.jpg'),
	(34, 'Архитектурные фантазии: 8', 'Я. Г. Чернихов', '1933', 'Из цикла „Сказки индустрии“. Открытая фермовая композиция транспортеров. Сильно выраженная конструкция в пространственном разрешении. Цветовая гамма в четыре краски.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_008.jpg'),
	(35, 'Архитектурные фантазии: 9', 'Я. Г. Чернихов', '1933', 'Мощная грандиозная открытая механическая установка из монолитных спаянных элементов. Сочетание объемов в объединении с фермовыми пространственными конструкциями.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_009.jpg'),
	(36, 'Архитектурные фантазии: 10', 'Я. Г. Чернихов', '1933', ' Город гигантов-небоскребов в композиции, выраженной по вертикали. Цветовая гамма в теплых оранжево-желтых тонах. Условно-графическая фоновая обработка.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_010.jpg'),
	(37, 'Архитектурные фантазии: 11', 'Я. Г. Чернихов', '1933', 'Конструктивно-скомплектованные массивы сооружений гражданского типа с отдельными группировками основных центров. Яркая цветовая иллюминовка объемно-пространственных решений. Наземные сухопутные мостовые сооружения. Из цикла „Город будущего“.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_011.jpg'),
	(38, 'Архитектурные фантазии: 12', 'Я. Г. Чернихов', '1933', 'Композиция выявления сооружений сгруппированного типа с выраженной устремленностью. Цветовая двухколоритная строгая гамма.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_012.jpg'),
	(39, 'Архитектурные фантазии: 13', 'Я. Г. Чернихов', '1933', 'Сложное пространственное объединение ряда сооружений-небоскребов в единое цельное завершение. Цветная иллюминовка вибрирующего характера музыкальной гаммы тонов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_013.jpg'),
	(40, 'Архитектурные фантазии: 14', 'Я. Г. Чернихов', '1933', 'Архитектурная композиционная надуманность из многоразличных составляющих. Усложненная комбинация различных объемов сооружений с явной цветовой декоративностью.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_014.jpg'),
	(41, 'Архитектурные фантазии: 15', 'Я. Г. Чернихов', '1933', 'Город гигантов-небоскребов в композиции выраженной по вертикали. Цветовая гамма в холодных зелено-синих и сероватых тонах.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_015.jpg'),
	(42, 'Архитектурные фантазии: 16', 'Я. Г. Чернихов', '1933', 'Пространственная театральная композиция из конструктивно объединенных линеарных элементов с явными динамическими признаками.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_016.jpg'),
	(43, 'Архитектурные фантазии: 17', 'Я. Г. Чернихов', '1933', 'Архитектурная выдумка-иллюзия. Выявленная устремленность, демонстрация величественности массы и передача грандиозности помощью кольцевых, криволинейных и прямолинейных сочетаний с их сочной окраской.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_017jpg'),
	(44, 'Архитектурные фантазии: 18', 'Я. Г. Чернихов', '1933', 'Динамика вертикали и горизонтали. Демонстрация устремленности и величественности. Композиция из круговых линейных колец, кривых и прямых линий.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_018.jpg'),
	(45, 'Архитектурные фантазии: 19', 'Я. Г. Чернихов', '1933', 'Композиция линейного порядка сочетания кривых и прямых. Демонстрация величественных масс с выраженной динамикой веса—тяжести.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_019.jpg'),
	(46, 'Архитектурные фантазии: 20', 'Я. Г. Чернихов', '1933', 'Композиционное измышление сложнейшего сочетания объемов в линеарном отображении. Сильно-выраженная пространственность. Теплая гамма раскраски.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_020.jpg'),
	(47, 'Архитектурные фантазии: 21', 'Я. Г. Чернихов', '1933', 'Пространственно-решетчатая линеарная композиция. Демонстрация величественных монолитов театрально-архитектурного порядка в цветовой гамме. Стержневое конструктивное сочетание элементов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_021.jpg'),
	(48, 'Архитектурные фантазии: 22', 'Я. Г. Чернихов', '1933', 'Явно-конструктивное сооружение-установка. Примерное выявление устойчивости, упора, массивности и четкой спаянности элементов между собою. Подавляюще, угрюмое впечатление от всего здания.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_022.jpg'),
	(49, 'Архитектурные фантазии: 23', 'Я. Г. Чернихов', '1933', 'Сложная композиция заводского сооружения в условно-цветовом отображении. Сочетание стержневых, рамных и монолитных конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_023.jpg'),
	(50, 'Архитектурные фантазии: 24', 'Я. Г. Чернихов', '1933', 'Стройная гамма объединенных зданий с отображенной конструктивностью. Показательное одновременное объединение устремленности и статичности форм. Упрощенный способ иллюминовки сооружений.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_024.jpg'),
	(51, 'Архитектурные фантазии: 25', 'Я. Г. Чернихов', '1933', 'Завод конвеерной системы электролитической обработки предметов точной механики. Композиция простейших архитектурных форм в одно согласное и цельное сооружение с двуцветной контрастной окраской.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_025.jpg'),
	(52, 'Архитектурные фантазии: 26', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение комбината летучих газов и масел. Демонстрация сферических сооружений. Иллюминовка в холодных зелено-синих тонах. Показательное выявление плана.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_026.jpg'),
	(53, 'Архитектурные фантазии: 27', 'Я. Г. Чернихов', '1933', 'Замкнутая композиция заводского сооружения. Спаянная конструкция массивов здания в обрамлении фермовых кранов и эстакад. Резкая цветовая и тоновая иллюминовка.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_027.jpg'),
	(54, 'Архитектурные фантазии: 28', 'Я. Г. Чернихов', '1933', 'Фантастическая композиция организации пространства сложных по форме и сочетанию элементов сооружения. Выразительная концентрация составляющих и показательная демонстрация своеобразных сочетаний масс сооружения. Сильно выраженная динамичность.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_028.jpg'),
	(55, 'Архитектурные фантазии: 29', 'Я. Г. Чернихов', '1933', 'Ритмичное сочетание ряда корпусов сооружений в спокойных, завершенных простейших формах. Убедительная выразительность общей массы здания на красочном фоне.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_029.jpg'),
	(56, 'Архитектурные фантазии: 30', 'Я. Г. Чернихов', '1933', 'Строгое объединение отдельных звеньев сооружения в цельное, согласованное здание. Наглядные признаки упрощенных конструкций спокойной гаммы тонов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_030.jpg'),
	(57, 'Архитектурные фантазии: 31', 'Я. Г. Чернихов', '1933', 'Строгое объединение отдельных звеньев сооружения в цельное и компактное здание. Упрощенная форма в статическом решении. Подчеркнутые горизонтальные тяги и вертикальная устремленность обработки угла здания.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_031.jpg'),
	(58, 'Архитектурные фантазии: 32', 'Я. Г. Чернихов', '1933', 'Показательная иллюстрация компактной архитектурной задачи объединения зданий в одно целостное сооружение. Согласование упрощенных форм с величественностью.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_032.jpg'),
	(59, 'Архитектурные фантазии: 33', 'Я. Г. Чернихов', '1933', 'Компактно-конструктивное сооружение монолитного характера. Сочетание законченных разнохарактерных объемов зданий с четкой их подразделенностью на основные массы.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_033.jpg'),
	(60, 'Архитектурные фантазии: 34', 'Я. Г. Чернихов', '1933', 'Строгое конструктивно-компактное сочетание архитектурных элементов простейшего вида объемов в здание фабрично-заводского типа. Двухцветная окраска по плоскостям здания с отмывкой „на-нет“.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_034.jpg'),
	(61, 'Архитектурные фантазии: 35', 'Я. Г. Чернихов', '1933', 'Сочетание глухих массивов сооружений с фермовыми пространственными конструкциями. Передача мрачного, подавляющего впечатления при случайном объединении зданий между собою в тесно-сгруппированном виде.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_035.jpg'),
	(62, 'Архитектурные фантазии: 36', 'Я. Г. Чернихов', '1933', 'Выразительная демонстрация пространственных стержневых конструкций совместно с простейшей формы зданий спокойного характера.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_0306jpg'),
	(63, 'Архитектурные фантазии: 37', 'Я. Г. Чернихов', '1933', 'Композиция сложного сочетания объемов сооружений с решетчато-пространственными конструкциями. Устойчивая гамма составляющих элементов сконцентрированного порядка.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_037.jpg'),
	(64, 'Архитектурные фантазии: 38', 'Я. Г. Чернихов', '1933', 'Объединенное сочетание простейших массивов с фермовыми конструкциями. Выявление типа сооружений на стойках—колоннах. Уравновешенное сочетание зданий между собою.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_038.jpg'),
	(65, 'Архитектурные фантазии: 39', 'Я. Г. Чернихов', '1933', 'Характерно выявленная пространственность сооружений на мощных фермово-решетчатых стойках. Согласованное конструктивное объединение частей зданий между собою.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_039.jpg'),
	(66, 'Архитектурные фантазии: 40', 'Я. Г. Чернихов', '1933', 'Композиция завода специального назначения. Стройная организация пространства ритмически-согласованных элементов стержневого характера с дополнением сплошных массивов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_040.jpg'),
	(67, 'Архитектурные фантазии: 41', 'Я. Г. Чернихов', '1933', 'Фабрично-заводская архитектурная композиция с превалированием вертикальных элементов и конструкций. Четкая, строгая задача объединения сплошных масс с большим количеством фермово-решетчатых сооружений.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_041.jpg'),
	(68, 'Архитектурные фантазии: 42', 'Я. Г. Чернихов', '1933', 'Демонстрация конструктивно-объединенных монолитных элементов сооружения. Выраженная тяжеловесность единства масс и массивная упрощенность участвующих частей.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_042.jpg'),
	(69, 'Архитектурные фантазии: 43', 'Я. Г. Чернихов', '1933', ' Сложная конструктивная композиция в аксонометрическом изображении. Применение утрированно-удлиненных элементов сооружений прямолинейного и криволинейного характера надуманного сочетания.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_043.jpg'),
	(70, 'Архитектурные фантазии: 44', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение функционального сооружения фабрично-заводского типа. Объемно-плановая показательная задача.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_044.jpg'),
	(71, 'Архитектурные фантазии: 45', 'Я. Г. Чернихов', '1933', 'Стройная, согласованная композиция объемно-планового характера на тему „Испытательная станция-институт“. Аксонометрическое отображение в сильной цветовой иллюминовке с условно-искусственным графическим приемом.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_045.jpg'),
	(72, 'Архитектурные фантазии: 46', 'Я. Г. Чернихов', '1933', 'Объемно-плановая показательная архитектурная композиция. Демонстрация объединенных элементов с выраженной пространственностью.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_046.jpg'),
	(73, 'Архитектурные фантазии: 47', 'Я. Г. Чернихов', '1933', 'Объемно-плановая архитектурная композиция в аксонометрическом изображении. Искусственно-утрированный прием отображения сооружений с особой точки зрения. Простейшая цветовая гамма тонов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_047.jpg'),
	(74, 'Архитектурные фантазии: 48', 'Я. Г. Чернихов', '1933', 'Гармонично-скомплектованное объединение сооружений в целостное и связное сочетание объемно-пространственных элементов. Четко выраженная планировка зданий. Бодряще-свежая иллюминовка изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_048.jpg'),
	(75, 'Архитектурные фантазии: 49', 'Я. Г. Чернихов', '1933', 'Архитектурное измышление усложненного типа. Объединение на конструктивных началах криволинейных форм с прямолинейными.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_049.jpg'),
	(76, 'Архитектурные фантазии: 50', 'Я. Г. Чернихов', '1933', 'Аксонометрическая композиция комбината специального назначения. Строгое построение планировки. Окраска в спокойной гамме тонов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_050.jpg'),
	(77, 'Архитектурные фантазии: 51', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение фантастического комбината-завода с надуманной магистралью. Теплая гамма тонов (красн., желт., сер.)', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_051.jpg'),
	(78, 'Архитектурные фантазии: 52', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение механизированного химического комбината с выраженной магистральностью. Условный прием графического отображения. Пример четкого удробленного изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_052.jpg'),
	(79, 'Архитектурные фантазии: 53', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение надуманного соцгорода в комбинации упрощенных объемов и магистралей движения. Сочетание различной величины зданий в некоторой предумышленной расстановке.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_053.jpg'),
	(80, 'Архитектурные фантазии: 54', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение упрощенных объемов сооружений в конструктивно спаянном решении. Показательное выявление планировки участка в некотором сплошном сочетании групп зданий.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_054.jpg'),
	(81, 'Архитектурные фантазии: 55', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение функционально-разрешенного завода-города с уточненной магистралью. Упрощенный, условный способ отображения в некотором сложном сочетании групп зданий.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_055.jpg'),
	(82, 'Архитектурные фантазии: 56', 'Я. Г. Чернихов', '1933', 'Архитектурный набросок чисто-фантастического порядка. Признаки явной надуманности и архитектурного измышления. Искусственный прием ярко-цветистого отображения фантазии на черном фоне.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_056.jpg'),
	(83, 'Архитектурные фантазии: 57', 'Я. Г. Чернихов', '1933', 'Архитектурная аксонометрия в планово-объемном решении. Группировка сооружений в функциональном ритмическом построении.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_057.jpg'),
	(84, 'Архитектурные фантазии: 58', 'Я. Г. Чернихов', '1933', 'Своеобразно-утрированная аксонометрия нового индустриального города с выраженной узловой магистралью. Надуманное изобразительное выражение плановой задачи с цветовой гаммой.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_058.jpg'),
	(85, 'Архитектурные фантазии: 59', 'Я. Г. Чернихов', '1933', 'Пространственно-сложная театральная цветовая композиция из сочетания изогнутых поверхностей и прямоугольных плоскостей.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_059.jpg'),
	(86, 'Архитектурные фантазии: 60', 'Я. Г. Чернихов', '1933', 'Цветная линейно-графическая композиция из ряда стержневых вышек. Выявление пространственности с помощью жестких конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_060.jpg'),
	(87, 'Архитектурные фантазии: 61', 'Я. Г. Чернихов', '1933', 'Архитектурная композиция деревянных вышек. Заведомо надуманная холодная гамма окраски участвующих элементов. Демонстрация ажурности конструктивных элементов помощью нагроможденных частей.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_061.jpg'),
	(88, 'Архитектурные фантазии: 62', 'Я. Г. Чернихов', '1933', 'Фермовые упоры-стойки (электромачты). Пример выявления пространства и композиционной трактовки металлических конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_062.jpg'),
	(89, 'Архитектурные фантазии: 63', 'Я. Г. Чернихов', '1933', ' Жесткая упрощенная конструкция металлических и железобетонных элементов с цветовой иллюминовкой ограниченного колорита красок.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_063\\.jpg'),
	(90, 'Архитектурные фантазии: 64', 'Я. Г. Чернихов', '1933', 'Показательная демонстрация перспективного изображения скомплектованных сооружений с помощью линеарного изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_064.jpg'),
	(91, 'Архитектурные фантазии: 65', 'Я. Г. Чернихов', '1933', 'Показательная демонстрация конструктивно-пространственного сочетания сооружений с помощью цветного линеарного изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_065.jpg'),
	(92, 'Архитектурные фантазии: 66', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение функционально связанного конгломерата сооружений с помощью иллюминованного цветного линеарного выражения. Примерное упражнение пространственной задачи.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_066.jpg'),
	(93, 'Архитектурные фантазии: 67', 'Я. Г. Чернихов', '1933', 'Линеарное разрешение пространственной архитектурной композиции, своеобразно-бодряще иллюстрирующей в графическом отображении сложную комбинацию участвующих объектов в аксонометрии.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_067.jpg'),
	(94, 'Архитектурные фантазии: 68', 'Я. Г. Чернихов', '1933', 'Архитектурная выдумка, показательно иллюстрирующая сложную особую композиционную трактовку изображения. Выявленное пространство. Условное сочетание заранее заданных красок построения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_068.jpg'),
	(95, 'Архитектурные фантазии: 69', 'Я. Г. Чернихов', '1933', 'Сложное здание специального назначения с явно выраженной пространственностью. Четко-конструктивная связь стержневых и массивных элементов между собою.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_069.jpg'),
	(96, 'Архитектурные фантазии: 70', 'Я. Г. Чернихов', '1933', 'Строго обоснованное сочетание массивов сооружения функциональной фабрики поточной системы производства. Сочетание спокойных, уравновешенных форм с реальной холодной окраской последних.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_070.jpg'),
	(97, 'Архитектурные фантазии: 71', 'Я. Г. Чернихов', '1933', 'Сложная комбинация совместного объединения скомплектованных мощных зданий и гигантских фермовых сооружений. Излишняя скученность зданий. Мягкая гамма расцветки участвующих элементов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_071.jpg'),
	(98, 'Архитектурные фантазии: 72', 'Я. Г. Чернихов', '1933', 'Фермовая величественная конструкция в объединении с четким статистическим сооружением заводского характера. Панорамное выявление заднего плана передним. Контрастная цветовая иллюминовка.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_072.jpg'),
	(99, 'Архитектурные фантазии: 73', 'Я. Г. Чернихов', '1933', 'Завод-монолит. Объединенное сочетание мощных дымоотводов, цилиндрических массивов, сложных замкнутых ферм и прямоугольных массивов. Воплощение современного завода в грандиозных, строго-скомплектованных конструкциях. Спокойная четкая окраска.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_073.jpg'),
	(100, 'Архитектурные фантазии: 74', 'Я. Г. Чернихов', '1933', 'Усложненная комбинация конструктивно-сочлененных ребристых элементов с выраженной пространственностью и динамикой. Теплая цветовая окраска.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_074.jpg'),
	(101, 'Архитектурные фантазии: 75', 'Я. Г. Чернихов', '1933', 'Строгое, четкое и компактное объединение зданий заводского характера. Ритмично и последовательно спаянные элементы сооружения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_075.jpg'),
	(102, 'Архитектурные фантазии: 76', 'Я. Г. Чернихов', '1933', 'Сгруппированная масса сооружений сложно-изгибающихся тел вращения с максимальным обогащением дополнительных конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_076.jpg'),
	(103, 'Архитектурные фантазии: 77', 'Я. Г. Чернихов', '1933', 'Показательный архитектурный экстерьер с четко-выраженной конструкцией и выпуклой динамикой архитектурного фона. Примерный случай композиционного изображения заднего и переднего плана.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_077.jpg'),
	(104, 'Архитектурные фантазии: 78', 'Я. Г. Чернихов', '1933', 'Фантастическая явно-надуманная композиция. Условный способ отображения с заранее надуманными цветовыми контрастами изображения и фона. Упрощенный передний и усложненный задний план.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_078.jpg'),
	(105, 'Архитектурные фантазии: 79', 'Я. Г. Чернихов', '1933', 'Сложная конструктивная композиция мощных устоев-быков сооружений и открытых установок. Цветовая яркая окраска части зданий.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_079.jpg'),
	(106, 'Архитектурные фантазии: 80', 'Я. Г. Чернихов', '1933', 'Показательная конструктивная композиция завершенного типа усложненной формы сооружений. Сочная тяжеловесная окраска. Стройная ритмичная перспектива сооружений. Из цикла „Сказки индустрии“.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_080.jpg'),
	(107, 'Архитектурные фантазии: 81', 'Я. Г. Чернихов', '1933', 'Показательная конструктивная композиция завершенного типа усложненных форм сооружений. Из цикла „Сказки индустрии”. Смешение сплошных масс с формовыми элементами. Удробленный задний план.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_081.jpg'),
	(108, 'Архитектурные фантазии: 82', 'Я. Г. Чернихов', '1933', 'Архитектурная выдумка индустриального порядка. Пространственная конструкция с показательной стержневой системой элементов выпирающего типа. Демонстрация усиленной динамики в сооружении.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_082.jpg'),
	(109, 'Архитектурные фантазии: 83', 'Я. Г. Чернихов', '1933', 'Композиция на индустриальную тему. Мощные экскаваторы на фоне фабрично-заводских сооружений. Одноцветная гамма холодных тонов экскаваторов. Законченный передний и схематичный задний план.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_083.jpg'),
	(110, 'Архитектурные фантазии: 84', 'Я. Г. Чернихов', '1933', 'Образцовая архитектурная композиция, демонстрирующая сложнейшее объединение зданий между собою в некоторое цельное сочетание сооружения. Показательная архитектурная цветная графика.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_084.jpg'),
	(111, 'Архитектурные фантазии: 85', 'Я. Г. Чернихов', '1933', 'Сложнейшая композиционная архитектурная задача. Показательное выявление объединения сплошных конструктивных элементов с решетчатыми фермами. Панорамный сложный характер изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_085.jpg'),
	(112, 'Архитектурные фантазии: 86', 'Я. Г. Чернихов', '1933', 'Явно выраженная пространственная архитектурная композиция в аксонометрическом отображении. Спокойная гамма холодных тонов.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_086.jpg'),
	(113, 'Архитектурные фантазии: 87', 'Я. Г. Чернихов', '1933', 'Музыкальная архитектурная композиционная выдумка иллюзорного порядка. Согласованный ритм построения и расцветки изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_087.jpg'),
	(114, 'Архитектурные фантазии: 88', 'Я. Г. Чернихов', '1933', 'Планово-пространственная архитектурная композиция в аксонометрическом решении. Искусственный графический прием изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_088.jpg'),
	(115, 'Архитектурные фантазии: 89', 'Я. Г. Чернихов', '1933', 'Надуманная сложная пространственная композиция линеарного порядка. Комбинация кривых и прямых с цветовой иллюминовкой смешанного типа.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_089.jpg'),
	(116, 'Архитектурные фантазии: 90', 'Я. Г. Чернихов', '1933', 'Промышленное здание с характерными консольными элементами сооружений. Сгруппированная масса объемов замкнутого характера с фермовыми придатками.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_090.jpg'),
	(117, 'Архитектурные фантазии: 91', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение конструктивно-спаянного сооружения промышленного характера. Тяжелая расцветка архитектурной композиции в глухих тонах при яркой частичной окраске фона.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_091.jpg'),
	(118, 'Архитектурные фантазии: 92', 'Я. Г. Чернихов', '1933', 'Аксонометрическое изображение сложного конструктивно-спаянного сооружения функционального завода. Легкая расцветка архитектурного построения в холодной гамме тонов и теплого фона.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_092.jpg'),
	(119, 'Архитектурные фантазии: 93', 'Я. Г. Чернихов', '1933', 'Центральное книгохранилище и библиотека дома техники. Сочетание простейших объемов в целостное компактное сооружение монолитного характера с окраской в теплых тонах.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_093.jpg'),
	(120, 'Архитектурные фантазии: 94', 'Я. Г. Чернихов', '1933', 'Научно-исследовательская лаборатория комбината тяжелой промышленности. Объединение простейших объемов здания в единое и цельное сооружение. Выявление на фасаде рамных жел.-бет. конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_094.jpg'),
	(121, 'Архитектурные фантазии: 95', 'Я. Г. Чернихов', '1933', 'Явно-надуманная архитектурная композиция в аксонометрическом построении. Пример условной трактовки изображения и ее иллюминовки. Демонстрация натяжных струнных шатровых конструкций.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_095.jpg'),
	(122, 'Архитектурные фантазии: 96', 'Я. Г. Чернихов', '1933', 'Сложная композиционная архитектурная задача, иллюстрирующая наглядно пространственность. Показательно-условный прием расположения изображения специальных ферм на плоскости чертежа.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_096.jpg'),
	(123, 'Архитектурные фантазии: 97', 'Я. Г. Чернихов', '1933', 'Сложная композиционная графико-линейная фермовая задача. Выраженная пространственность в оригинальной трактовке изображения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_097.jpg'),
	(124, 'Архитектурные фантазии: 98', 'Я. Г. Чернихов', '1933', ' Демонстрация архитектурного сооружения усиленной динамики. Ракурсное отображение произведения с особой импозантной точки зрения. Выраженная аффектация светотени искусственным приемом.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_098.jpg'),
	(125, 'Архитектурные фантазии: 99', 'Я. Г. Чернихов', '1933', 'Сооружение надземного характера с выявлением жестких фермовых поддерживающих частей. Утрированное выделение мощного переднего плана. Условная иллюминовка архитектурной композиции.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_099.jpg'),
	(126, 'Архитектурные фантазии: 100', 'Я. Г. Чернихов', '1933', 'Композиционное сочетание переднего и заднего плана. Показательная демонстрация особых зрительных точек архитектурно-пространственного ансамбля. Панорамная живописная композиция.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_100.jpg'),
	(127, 'Архитектурные фантазии: 101', 'Я. Г. Чернихов', '1933', 'Архитектурный интерьер промышленного характера. Спаянно-конструктивное объединение особо-сложных элементов сооружения.', 'http://ussr.totalarch.com/sites/default/files/gallery/101chernikhov_101.jpg'),
	(128, 'Точильщик (Принцип мелькания)', 'К.С. Малевич', '1912', 'В единении футуристической энергии и кубистической структурности заключена новизна кубофутуризма и предвестие супрематической концепции Малевича.', 'https://upload.wikimedia.org/wikipedia/commons/5/58/The_Knife_Grinder_Principle_of_Glittering_by_Kazimir_Malevich.jpeg'),
	(129, 'На жатву (Марфа и Ванька)', 'К.С. Малевич', '1928 - 1929', 'Две фигуры обращены к зрителю спиной. Центральное место занимает женщина Марфа, идущая на жатву. Её формы исполнены в той же манере, что и фигура Ваньки, только она намного больше его пропорционально.', 'https://upload.wikimedia.org/wikipedia/commons/5/56/Kazimir_malevich%2C_verso_la_vendemmia_%28martha_e_vanka%29%2C_1928-29.JPG'),
	(130, 'Утро после вьюги в деревне', 'К.С. Малевич', '1912', 'На создание этой картины, по мнению искусствоведов, Малевича вдохновили сразу три художника. Одним из них был Камиль Писсарро и его серия работ, посвященных деревьям и лугам Эраньи в разные часы дня и времена года. Вторым стал Фернан Леже, ранние кубистические картины которого Малевич мог видеть на выставке в Москве в 1912 году. Ну и, наконец, не обошлось здесь и без влияния Поля Сезанна, которого еще Пикассо называл своим единственным учителем и вдохновителем.', 'https://psv4.userapi.com/c532036/u116625127/docs/d35/0da665293ef7/unnamed.jpg'),
	(131, 'Композиция с Моной Лизой', 'К.С. Малевич', '1914', '«Композиция с Джокондой» и другие работы Малевича 1914 года становятся своего рода предвестниками грядущего супрематизма. Пройдет совсем немного времени, и художник взорвет живописный мир «Черным квадратом».', 'https://thesketchline.com/wp-content/uploads/2019/05/%D1%81-1.jpeg'),
	(132, 'Спортсмены', 'К.С. Малевич', '1931', 'Все фигуры полностью подчинены цветовой композиции. Вся композиция кажется полностью отчужденной от реальности. Малевич изображал спортсменов без лиц, чтобы подчеркнуть смысл мира. Они представлены не как обычные люди, а как люди, связанные с искусством.', 'https://thesketchline.com/wp-content/uploads/2019/05/%D0%B0-8.jpeg'),
	(133, 'Голова крестьянина', 'К.С. Малевич', '1929', 'Нового крестьянина Малевича едва ли можно назвать счастливым или умиротворенным. Его судьба незавидна и горька, как и судьба великого множества деревенских жителей в те годы. За спиной героя картины видны силуэты сельских построек, над которыми кружат птицы, а с другой стороны полотна к ним неумолимо приближаются самолеты, как некие предвестники опасности.', 'https://uploads1.wikiart.org/images/kazimir-malevich/head-of-a-peasant-1929.jpg!Large.jpg'),
	(134, 'Косарь', 'К.С. Малевич', '1913', '«Косарь» относится к кубистическому периоду. Кубизм зарождается во Франции, его начало связано с картиной Пабло Пикассо «Авиньонские девицы». Картины кубистов отличаются геометризацией: формы упрощаются, предметы раскладываются на геометрические элементы.', 'http://artmuseumnn.ru/upload/image/%D0%9C%D0%B0%D0%BB%D0%B5%D0%B2%D0%B8%D1%87%20%D0%9A%D0%BE%D1%81%D0%B0%D1%80%D1%8C.jpg'),
	(135, 'Женщина с граблями', 'К.С. Малевич', '1932', '«Женщина с граблями» — это картина из второй серии крестьянского цикла работ Казимира Малевича. Художник принялся за её создание в 1930-1931 годах, после того, как первый цикл был утерян после Берлинской и Варшавской выставок в 1927 году.', 'https://upload.wikimedia.org/wikipedia/commons/0/04/%D0%96%D0%B5%D0%BD%D1%89%D0%B8%D0%BD%D0%B0_%D1%81_%D0%B3%D1%80%D0%B0%D0%B1%D0%BB%D1%8F%D0%BC%D0%B8.png'),
	(136, 'Солдат первой дивизии.', 'К.С. Малевич', '1914', 'Создавая эту картину Малевич вновь прибегает к использованию цветовых пятен для создания общего ощущения – собранные в изображении цвета характерны для определенного рода войск.', 'https://regnum.ru/uploads/pictures/news/2018/03/02/regnum_picture_1520013996447231_normal.jpg'),
	(137, 'Корова и скрипка', 'К.С. Малевич', '1913', 'Работа «Корова и скрипка» одна из самых ярких и необычных картин. Написана картина на деревянной дощечке. В то время художник испытывал нужду и не мог позволить себе купить дорогой холст. Тогда он использовал в качестве холста часть деревянной полки, на которой даже остались отверстия для крепления.', 'https://opisanie-kartin.com/pictures/3/image127.jpg'),
	(138, 'Красный дом', 'К.С. Малевич', '1932', 'Казимиру Малевичу почему-то пришло в голову обособить дом в отдельную цельную картину. Цели не очень понятны, однако художнику видней. Точно такой же дом уже встречался на картинах Казимира, таких как «Сложное предчувствие» и «Бегущий человек». ', 'https://opisanie-kartin.com/pictures/8/image099.jpg'),
	(139, 'Пейзаж с пятью домами', 'К.С. Малевич', '1932', '«Пейзаж с пятью домами», возможно, является произведением, которым автор стремится обратить внимание зрителя на гармоничность сосуществования человека с природой, которую ни в коем случае нельзя терять. Можно предположить, что пять зданий без дверей и окон, стоящие вдоль прямого горизонта, не должны вызывать мрачных ассоциаций, а лишь абстрактно показывать результат человеческой деятельности.', 'https://opisanie-kartin.com/uploads10/image012.jpg'),
	(140, 'Голова крестьянской девушки', 'К.С. Малевич', '1913', '«Голова крестьянской девушки» — одно из полотен, замыкавших первый крестьянский цикл. Картина была написана в 1913 году, однако сам Малевич датировал её 1912 годом. В «Голове крестьянской девушки» он соединяет абстрактное (геометрическое) и изобразительное, а для обозначения этого союза берёт термин «супранатурализм», то есть соединяющее супрематизм и натурализм.', 'https://upload.wikimedia.org/wikipedia/commons/6/61/Head_of_a_Peasant_Girl.jpg'),
	(141, 'Девушки в поле', 'К.С. Малевич', '1928 - 1929', 'В картине "Девушки в поле" сохраняется геометрическая основа в построении фигур, выработанная К.С.Малевичем в начале 1910-х гг. И в целом полотно "Девушки в поле" перекликается с ранними работами Малевича. Это не удивительно: в 1920-е гг. художник нередко обращался к повторению разных мотивов, вероятно, вследствие того, что выставка в Берлине в 1927 г., охватывающая в основном ранний период творчества Малевича, по ряду причин осталась за рубежом.', 'https://rusmuseumvrm.ru/data/collections/painting/19_20/malevich_ks_devushki_v_pole_1928_1929_zh_9433/5977_mainfoto_03.jpg'),
	(142, 'Лесоруб', 'К.С. Малевич', '1912 - 1913', 'Достаточно известная работа талантливого художника Казимира Малевича «Лесоруб» была создана им на протяжении 1912-1913 годов. В качестве красок художник предпочел масло. Эту картину он называл «Подражанием иконе», подразумевая под этим ее схожесть с Этюдами фресковой живописи.', 'https://opisanie-kartin.com/uploads20/image118.jpg'),
	(143, 'Авиатор', 'К.С. Малевич', '1914', 'В "Авиаторе" художник создает свою систему не столько видимого, сколько расчисленного, изобретенного мира, существующего по своим, не подвластным обывательскому "здравому смыслу" законам. Более того, он охотно использует то, что можно было бы назвать игрой слов. Так, белый силуэт рыбы, пересекающий словно парящую в тяжелом, насыщенном кубистическом пространстве фигуру авиатора, приводит на память слово "воздухоплавание".', 'https://rusmuseumvrm.ru/data/collections/painting/19_20/malevich_ks_aviator_1914_zhb_1348/5999_mainfoto_03.jpg'),
	(144, 'Портрет Ивана Васильевича Клюна (Усовершенствованный портрет Клюна (Строитель))', 'К.С. Малевич', '1913', '"Портрет Клюна" – один из наиболее устойчивых мотивов в творчестве Малевича. Фронтальное изображение мужского лица – "Крестьянина", "Строителя", "Православного"– традиционно связывается с именем одного из его ближайших друзей и последователей – И. В. Клюнкова (Клюна).', 'https://rusmuseumvrm.ru/data/collections/painting/19_20/zhb_1469/2317_mainfoto_03.jpg'),
	(145, 'Сложное предчувствие (Торс в желтой рубашке)', 'К.С. Малевич', '1932', 'В этой картине, датируемой 1928–1932 годом, как и в других работах второго крестьянского цикла, бесспорно отразилась трагедия времени. Малевич, ощущающий, что «идет на дно крестьянская Атлантида», а вместе с ней и его жизнь, написал на обороте холста горькие слова: «Композиция сложилась из элементов, ощущение пустоты, одиночества, безвыходности жизни».', 'https://rusmuseumvrm.ru/data/collections/painting/19_20/malevich_k.s._slozhnoe_predchuvstvie_tors_v_zheltoy_rubashke._okolo_1932._zh-9477/14532_mainfoto_03.jpg'),
	(146, '«На сенокосе» (Косарь)', 'К.С. Малевич', '1929', 'Казимир Северинович Малевич написал картину «На сенокосе» в 1929 году. И сейчас она хранится в Государственной Третьяковской галерее. А создана она в жанре супрематизма. На его картинах прослеживаются такие течения, как кубизм и футуризм, а также и абстрактное искусство.Суть данной картины является покой, который основан на геометрических формах.', 'https://opisanie-kartin.com/uploads15/image007.jpg'),
	(147, 'Англичанин в Москве', 'К.С. Малевич', '1914', '«Англичанин в Москве» создан в 1914 году, на пороге всех мыслимых революций и войн, перетряхнувших Россию и вывернувших её наизнанку. Он устремляется вверх по вертикали, центр его композиции, её опора – крест. Переращиваются рыба и сабля, сабля и свеча. Кресты образуют буквы «т», крест можно увидеть, вглядевшись в фигуру человека, которая служит словно бы фоном для обыденных, набранных, кажется, без всякой логики вещей.', 'https://opisanie-kartin.com/uploads18/image190.jpg'),
	(148, 'Женщина с вёдрами', 'К.С. Малевич', '1912', 'Стиль, известный как русский авангард характерен для искусства Российской Империи, а затем и Советского Союза в период с 1890 по 1930 год. В это время внутри авангардного движения создавалось множество подстилей, имеющих свои уникальные отличительные черты. Одним из таких подстилей и стал супрематизм — движение, основанное Казимиром Малевичем и характерное для всей его живописи, в том числе и для «Женщины с ведрами».', 'https://opisanie-kartin.com/pictures/14/image041.jpg'),
	(149, 'Портрет Матюшина', 'К.С. Малевич', '1913', 'В 1913 году знаменитый художник К. С. Малевич написал необычную картину «Портрет Матюшина» в стиле нового на тот момент художественного направления – кубофутуризма. В отличие от большинства его приверженцев, Малевич не использовал в своей работе только монохромные цвета. Он нарисовал ее маслом, применив богатую цветовую палитру и изобразив в разных уголках картины реалистичные элементы, прекрасно передав динамику композиции и совместив несовместимое.', 'https://opisanie-kartin.com/pictures/17/image162.jpg'),
	(150, 'Жизнь в большой гостинице', 'К.С. Малевич', '1914', 'Сегодня одной из самых популярных картин, находящихся в музейном собрании живописи стиля авангард – это картина известного по всему миру Казимира Малевича. Одновременно с этим картина жизни в большой гостинице" является одной из самых магических, таинственных и загадочных. В основном эта загадочность достигается за счет стиля и кубо-футуристического периода творчества этого художника.', 'https://opisanie-kartin.com/pictures/10/image062.jpg'),
	(151, 'Уборка ржи', 'К.С. Малевич', '1911', '«Уборка ржи» — это образчик российского кубофутуризма, взявшего начало у французских мастеров. Работа относится к крестьянской серии картин, запечатлевшей рутинный уклад сельской жизни. В этот период у Малевича произошел перелом в направлении художественного стиля.', 'https://opisanie-kartin.com/pictures/17/image109.jpg'),
	(152, 'Жница', 'К.С. Малевич', '1912', 'Образ картины, которая сумела стать былью – праздник труда. Здесь удачно сочетается колористический композиционный план картины. Взглянув на картину, мы видим, как художник опрокинул закон воздушной перспективы.', 'https://opisanie-kartin.com/pictures/18/image319.jpg'),
	(153, 'Автопортрет с пилой (Пильщик)', 'И.В. Клюн', '1914', 'В 1892 Клюн с отцом едет в Рамонь Воронежской губернии в 40 км от Воронежа в имение принца Ольденбургского, где работает на сахарном заводе и продолжает занятия рисованием.', 'https://upload.wikimedia.org/wikipedia/commons/8/8f/Non-Objective_%28Kliun%2C_1915%2C_16%29.jpg'),
	(154, 'Супрематическая композиция', 'И.В. Клюн', '1919', 'В феврале 1919 Клюн участвует в 5-й государственной выставке, а в апреле–мае – в 10-й государственной выставке «Беспредметное творчество и супрематизм» в Москве, где экспонирует шестьдесят два произведения под общим названием «Искусство цвета», в числе которых – супрематическая живопись и графика, а также беспредметная скульптура.', 'http://rusavangard.ru/upload/iblock/887/887e892404d4e23f795aae5724ce0574.jpg'),
	(155, 'Супрематическая композиция №6', 'И.В. Клюн', '1921', 'В начале 1920-х Клюн уходит от супрематизма и создаёт сферические композиции, в которых геометризм сменяется плавным перетеканием форм («Сферическое пространство». 1922. ГТГ; «Красный свет. Сферическая композиция». 1923. ГМСИ).', 'http://rusavangard.ru/upload/iblock/ed5/ed5f0162886af72eeab16b1253e6f8d5.jpg'),
	(156, 'Пробегающий', 'И.В. Клюн', '1915', 'В 1915 Клюн участвует в «Первой футуристической выставке “Трамвай В”» (март–апрель). Следуя Малевичу, работает в стиле кубофутуризма («Озонатор». 1914. ГРМ), но старается изобрести собственный вариант стиля, что ему блестяще удаётся в одном из самых известных его произведений – рельефе «Пробегающий пейзаж» (1914–1915. ГТГ).', 'http://rusavangard.ru/upload/iblock/ea2/ea28fa8ae896f420ec70d625c9fecebd.jpg'),
	(157, 'Композиция с тремя центрами', 'И.В. Клюн', '1919', 'В 1920 Клюн становится членом Инхука. Активно участвует в четырёхмесячной дискуссии «Анализ понятий конструкции и композиции и момент их разграничения». Весной 1921 вместе с А.Д.Древиным, Н.А.Удальцовой и Б.Д.Королёвым покидает Инхук из-за несогласия с приверженцами производственного искусства.', 'https://artpriced.ru/images/load/02e/02e3da663828f951a5c2ad5ceaa93368.jpg'),
	(158, 'Озонатор', 'И.В. Клюн', '1914', 'В 1900-е, подобно Малевичу, работал в традициях символизма и модерна (несомненно влияние М. А. Врубеля, В. Э. Борисова-Мусатова, Н. Д. Милиоти). В 1913 году сблизился (через Малевича) с петербургским авангардом в лице членов «Союза молодежи», поучаствовав в их последней выставке (1913/1914).', 'https://artpriced.ru/images/load/c98/c9876e83ed8d0946426d9891d712e8a7.jpg'),
	(159, 'Кувшин', 'И.В. Клюн', '1914', 'Как и Малевич, в то время Клюн увлекался кубофутуризмом, живописным и скульптурным (последним, вероятно, под влиянием В. Е. Татлина), показывая свои работы на всех авангардных выставках.', 'https://artpriced.ru/images/load/fb8/fb8c8c1888c017248fd17778a2b95b24.jpg'),
	(160, 'Сферическая беспредметная композиция', 'И.В. Клюн', '1922 - 1925', 'В середине 1920-х Клюн попадает под влияние пуризма в лице французских художников Амеде Озанфана и Пьера ­Жаннере (их работы он изучал в журнале Cahiers d’Art). Называет этот стиль «конструктивным реализмом», продолжает в нём работать до середины 1930-х («Второй интерьер». 1925–1932. ГМСИ).', 'https://artpriced.ru/images/load/3b0/3b089c7ab9c27b6045032b7cdb67ae64.jpg'),
	(161, 'Пейзаж (Пробегающий пейзаж)', 'И.В. Клюн', '1914 - 1915', 'В 1915 году принял участие в создании буклета А. Е. Крученых «Тайные пороки академиков» (М., 1916): ему принадлежат литография и статья «Примитив в 20 столетии». Направленная против символизма и декадентства, эта книга стала последней данью Клюна футуризму.', 'https://upload.wikimedia.org/wikipedia/commons/7/73/Landscape_Racing_By_%28Kliun%2C_1914%2C_18%29.jpg'),
	(162, 'Часовщик', 'И.В. Клюн', '1914', 'С этого времени Клюн – участник всех авангардных выставок. Он начинает подписывать свои картины псевдонимом «И.Клюн» (впервые – на выставке «Художники Москвы – жертвам войны» (ноябрь 1914 – январь 1915).', 'https://upload.wikimedia.org/wikipedia/commons/b/b6/Ivan_Vasilievich_Kliun_-_The_Clockmaker_%28Der_Uhrmacher-_L%27horloger%29_-_2007.85_-_Minneapolis_Institute_of_Arts.jpg'),
	(163, 'Супрематизм', 'И.В. Клюн', '1916', 'В 1916 году Клюн вошёл в возглавляемую Малевичем группу «Супремус», приняв участие в подготовке так и не изданного журнала того же названия. Вместе с другими художниками-супрематистами сотрудничал с кустарными артелями в деревнях Вербовка и Скопцы. Этот период его творчества является одним из самых ярких в его жизни.', 'https://upload.wikimedia.org/wikipedia/commons/b/b7/Suprematism_%28Kliun%2C_1915%2C_16%29.jpg'),
	(164, 'Портрет', 'И.В. Клюн', '1914', 'В 1915 году уже как последователь малевичевского супрематизма он принял участие в «Последней футуристической выставке картин 0,10», опубликовав в её каталоге свой манифест. С этого периода начинается период беспредметного творчества.', 'https://upload.wikimedia.org/wikipedia/commons/4/4f/%D0%98.%D0%92.%D0%9A%D0%BB%D1%8E%D0%BD_%D0%9F%D0%BE%D1%80%D1%82%D1%80%D0%B5%D1%82.jpg'),
	(165, 'Синий кувшин', 'Н.А. Удальцова', '1915', 'Начиная с 1913 складывается собственный кубистический стиль Удальцовой, основанный на парижском опыте кубизма, полученном главным образом от Ле Фоконье.', 'https://upload.wikimedia.org/wikipedia/commons/5/57/Udaltsova._Blue_jug._1915.jpg'),
	(166, 'Автопортрет с палитрой', 'Н.А. Удальцова', '1915', 'В первых кубистических работах Удальцовой («Швея». 1912–1913. ГТГ) композиционная жёсткость и упрощённость приёмов ещё выдают влияние учителя. Характерно, что выставочный дебют Удальцовой состоялся на «Бубновом валете» (1914. Москва), где также были представлены работы французского мастера.', 'https://upload.wikimedia.org/wikipedia/commons/1/1f/Udaltsova._Selfportrait_with_palette._1915.jpg'),
	(167, 'Живописное построение', 'Н.А. Удальцова', '1916', 'Но уже в 1914–1915 стилистика кубистических работ Удальцовой меняется: она преодолевает уроки академии «Ла Палет» и приходит к более изощрённому стилю в духе Пабло Пикассо («Натурщица. Кубистическая конструкция». 1914; «Ресторан». 1915. Обе – ГРМ; «Новь». 1914–1915. КОХМ).', 'https://upload.wikimedia.org/wikipedia/commons/d/dd/Udaltsova._Picturesque_construction._1916.jpg'),
	(168, 'Кухня', 'Н.А. Удальцова', '1915', 'Причастность к супрематизму связана для Удальцовой с приглашением от Н.М.Давыдовой в октябре 1916 принять участие в изготовлении декоративных рисунков для артели вышивальщиц «Вербовка». Удальцова, как и другие приглашённые художники, сделала ряд супрематических эскизов. ', 'https://upload.wikimedia.org/wikipedia/commons/c/c3/Kitchen._1915.jpg'),
	(169, 'Кубистическая композиция', 'Н.А. Удальцова', '1915', 'В марте 1917 вступила в союз «Свобода искусству» в Петрограде, в мае–июне стала одним из организаторов левой (молодой) федерации Профессионального союза художников-живописцев Москвы, осенью участвовала в работах по декорированию московского кафе «Питтореск», потом участвовала в выставке «Бубнового валета» (1917. Москва).', 'https://upload.wikimedia.org/wikipedia/commons/c/c8/Udaltsova._Cubist_composition._1915.jpg'),
	(170, 'Композиция', 'Н.А. Удальцова', '1916', 'В 1918 сотрудничала в газете «Анархия», работала во Всероссийской коллегии по делам изобразительного искусства, в Пролеткульте, артели мастеров. Экспонировала свои работы на 1-й выставке профсоюза (май–июль).', 'https://upload.wikimedia.org/wikipedia/commons/8/86/Udaltsova._Composition._1916.jpg'),
	(171, 'Ресторан', 'Н.А. Удальцова', '1915', 'В 1919 Удальцова преподавала в I ГСХМ сначала как ассистент Малевича, а затем самостоятельно, как руководитель мастерской. Однако вскоре освобождается от влияния Малевича и отказывается от участия в выставке «Беспредметное творчество и супрематизм».', 'http://rusavangard.ru/upload/iblock/240/2404cbcadcea7408594ff57ae6ec9aec.jpg'),
	(172, 'Швея', 'Н.А. Удальцова', '1912 - 1913', 'В 1922 картины Удальцовой были показаны на Первой русской художественной выставке в Берлине. Несколько работ было приобретено Анонимным обществом (Société anonyme).', 'http://rusavangard.ru/upload/iblock/686/686c0b7df8e51ac567b99379281a8596.jpg'),
	(173, 'Красная фигура', 'Н.А. Удальцова', '1914 - 1915', 'В ноябре 1912 г. Надежда Удальцова, Любовь Попова, Вера Пестель и Софья Каретникова отправились в Париж. Их поездка отозвалась в художественных мастерских Москвы каламбуром: «Вера, Надежда, Любовь и даже мать их Софья нас покинули, собрались в Париж, живописи учиться».', 'http://rusavangard.ru/upload/iblock/d59/d597d303a15ea63de458330533f28568.jpg'),
	(174, 'Натюрморт', 'Н.А. Удальцова', '1919', 'В 1913 г. Удальцова вернулась в Москву и работала в мастерской Татлина. В 1914 г. впервые экспонировала одну из своих кубистических композиций на выставке общества «Бубновый валет», а весной 1915 г. в Петрограде участвовала в 1-й футуристической выставке картин «Трамвай В».', 'http://rusavangard.ru/upload/iblock/406/406e4411a78d864b5b8152ee456f57fe.jpg'),
	(175, 'Натюрморт', 'Н.А. Удальцова', '1916', 'В 1915 г. Надежда Удальцова, как автор буклета «Владимир Евграфович Татлин» для издания «Новый журнал для всех», участвует в создании первого печатного документа, посвященного его творчеству. В 1916 г. она вошла в группу последователей Казимира Малевича «Супремус».', 'http://rusavangard.ru/upload/iblock/406/406e4411a78d864b5b8152ee456f57fe.jpg'),
	(176, 'Натюрморт. Молоток и кружка.', 'Н.А. Удальцова', '1914 - 1915', 'С 1917 г. после Февральской революции Надежда Удальцова совместно с Владимиром Татлиным и Георгием Якуловым активно участвует в создании московского профсоюза художников, начинает преподавать. «Эти годы напряженной организационной работы и борьбы в школе за новое искусство мало оставляли времени для живописи».', 'http://rusavangard.ru/upload/iblock/a1f/a1feb63007daa69e880a7b06f7db7d4e.jpg'),
	(177, 'Гитара. Фуга', 'Н.А. Удальцова', '1914', 'В 1920 г. Надежда Удальцова выходит замуж за художника Александра Древина, что становится ключевым событием в её дальнейшей личной и творческой судьбе. В начале 1920-х они вместе возвращаются к фигуративной живописи.', 'https://psv4.userapi.com/c536132/u84634434/docs/d36/a6a66e7c4c76/IMG_1872.jpg'),
	(178, 'За пианино', 'Н.А. Удальцова', '1915', 'В 1922—1923 гг. произведения Удальцовой и Древина экспонируются на 1-й выставке русского искусства в Берлине. Их работы приобретены обществом «Société Anonyme». В 1923 г. вместе с бывшими участниками группы «Бубновый валет» Удальцова и Древин экспонируются на «Выставке картин» в Москве.', 'https://psv4.userapi.com/c536132/u84634434/docs/d21/939607078592/IMG_1871.jpg'),
	(179, 'Натюрморт. Музыкальные инмтрументы', 'Н.А. Удальцова', '1915', 'В 1927—1931 гг. Удальцова была действительным членом Общества московских художников, в 1928 г. её работы экспонировались на 1-й выставке Общества, в 1931 г. они с Древиным вошли в состав участников выставки «Тринадцать».', 'https://thesketchline.com/wp-content/uploads/2019/11/1-250.jpg'),
	(180, 'Механистическая волна', 'А.В. Козина', '2018', 'Композиция из геометрических фигур.', 'https://psv4.userapi.com/c520036/u84634434/docs/d46/e1947e4b9bae/kompoz_sin.png?extra=-CEV97znjQf8ZrQrapABaz9XWzyzy6rlIYLd5jxPI05AZkVnBjA58Xf7xybMGfAgfTTeJYXRI5417Ci6LtOE1VfqHNOyRDPkA_AMiC1KszFu78Ue3T_eg1Od5oP0zpNpdnYcQgBcCFbb5i0sDMSp4P4');